mapper.select:
SELECT T1.*, T2.* FROM (SELECT B.BNUM AS bNum, B.TITLE, B.CONTENTS, B.READCNT AS readCnt, B.TNUM AS tNum, B.CATEGORY, B.REGID AS regId, B.REGDT AS regDt, B.MODID AS modId, B.MODDT AS modDt, B.rnum AS num FROM (SELECT ROWNUM AS rnum, A.* FROM(SELECT * FROM BOARD --SEARCH CONDITION
ORDER BY REGDT DESC) A WHERE ROWNUM <= (#{pageSize, jdbcType=NUMERIC} * (#{pageNum, jdbcType=NUMERIC} -1) + #{pageSize, jdbcType=NUMERIC}))B WHERE B.rnum >= (#{pageSize, jdbcType=NUMERIC} * (#{pageNum, jdbcType=NUMERIC} -1)+1)) T1 CROSS JOIN (SELECT COUNT(*) FROM (SELECT * FROM BOARD --SEARCH CONDITION
))T2;
