mapper.select:
SELECT T1.*, T2.* FROM ( SELECT B.rnum, B.saveFileNm, B.cartnum, B.bprice, B.pname, B.pnum, B.dprice, B.afterPrice, B.cartcnt, B.regid FROM( SELECT ROWNUM AS rnum, A.* FROM( SELECT i.save_file_nm as saveFileNm, c.cartnum as cartNum, p.bprice as bPrice, p.pname as pName, p.pnum as pNum, p.dprice as dPrice, p.bprice - (p.bprice * p.discount) as afterPrice, c.cartcnt as cartCnt, c.regid as regId FROM CART c JOIN PRODUCT p ON c.pnum = p.pnum JOIN IMAGE i ON c.pnum = i.refnum search condition
)A WHERE rownum <= (#{pageSize} * ( #{pageNum}-1)+ #{pageSize} ) )B WHERE B.rnum >= (#{pageSize} * (#{pageNum}-1)+1) )T1 CROSS JOIN( SELECT COUNT(*) total_cnt FROM CART c JOIN PRODUCT p ON c.pnum = p.pnum JOIN IMAGE i ON c.pnum = iz.refnum
)T2

mapper.update:
	
		UPDATE CART
		
		SET cartcnt = #{cartCnt, jdbcType=NUMERIC} 
		
		WHERE onum = #{oNum, jdbcType=NUMERIC}
	    AND	  regid = #{regId, jdbcType=VARCHAR}


	UPDATE CART 
	SET cartcnt = cartcnt + #{cartCnt, jdbcType=NUMERIC}
	WHERE regid = #{regId, jdbcType=VARCHAR}
	AND onum = #{oNum, jdbcType=NUMERIC}

mapper.insert:
INSERT INTO CART (
    ONUM,
    CARTCNT,
    REGID
) VALUES (
    #{oNum, jdbcType=NUMERIC},
    1,
    #{regId, jdbcType=VARCHAR}
)
