mapper.select:
SELECT T1.*,T2.* FROM ( SELECT B.pnum as pNum, B.pname as pName, B.bprice as bPrice, B.detail as detail, B.discount as discount, B.dprice as dPrice, B.pcategory as pCategory, B.regid as regId, CASE TO_CHAR(B.regdt, 'yyyy/mm/dd') when TO_CHAR(sysdate, 'yyyy/mm/dd') THEN TO_CHAR(sysdate, 'HH24:MI') ELSE TO_CHAR(B.regdt, 'yyyy/mm/dd') END AS regDt, B.modid as modId, CASE TO_CHAR(B.moddt, 'yyyy/mm/dd') when TO_CHAR(sysdate, 'yyyy/mm/dd') THEN TO_CHAR(sysdate, 'HH24:MI') ELSE TO_CHAR(B.moddt, 'yyyy/mm/dd') END AS modDt, B.rnum as num FROM ( SELECT ROWNUM AS rnum, A.* FROM ( SELECT * FROM PRODUCT WHERE 1=1 AND regdt > to_date('19700101','yyyymmdd')
ORDER BY regdt DESC )A WHERE rownum <= (#{pageSize} * (#{pageNum}-1)+#{pageSize}) ) B WHERE B.rnum >= (#{pageSize} * (#{pageNum}-1)+1) )T1 NATURAL JOIN ( SELECT COUNT(*) totalCnt FROM PRODUCT WHERE 1=1
)T2

SELECT
	
FROM PRODUCT
WHERE PNUM = #{pNum, jdbcType=VARCHAR}
