mapper.select:
SELECT
	
FROM OPT
WHERE ONUM = #{oNum, jdbcType=VARCHAR}

mapper.update:
UPDATE OPT
SET ONAME      = #{oName,jdbcType=VARCHAR},
    OPRICE     = #{oPrice,jdbcType=NUMERIC},
    PNUM       = #{pNum,jdbcType=NUMERIC},
    INUM       = #{iNum,jdbcType=NUMERIC}
WHERE ONUM = #{oNum, jdbcType=NUMERIC}
