mapper.select:
SELECT T1.*, T2.* FROM ( SELECT B.u_id, B.name, B.passwd, DECODE(B.h_level, 1, 'BASIC', 2, 'SILVER', 3, 'GOLD', 'NOLEVEL') as hlevel, B.login, B.recommend, B.email, TO_CHAR(B.reg_dt, 'YYYY/MM/DD') regDt, B.rnum AS num FROM( SELECT ROWNUM AS rnum, A.* FROM( SELECT * FROM users
ORDER BY reg_dt DESC )A WHERE ROWNUM <= ( #{pageSize} *( #{pageNum} - 1)+ #{pageSize} ) )B WHERE B.rnum >= ( #{pageSize} *( #{pageNum} - 1)+1) ) T1 NATURAL JOIN (SELECT COUNT(*) totalCnt FROM users
)T2

mapper.delete:
 
		
			DELETE 
